import { useState, useCallback } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { motion } from "framer-motion";
import { Fade } from "../Transition";
import validator from "validator";
import cx from "classnames";
import style from "./Header.module.css";

const formSignup = {
  username: { value: "", isValid: true, message: "" },
  email: { value: "", isValid: true, message: "" },
  password: { value: "", isValid: true, message: "" },
  confirmPassword: { value: "", isValid: true, message: "" },
};

const Signin = (props) => {
  const [signup, setSignup] = useState(formSignup);

  const submitHandler = (event) => {
    event.preventDefault();
    resetValidation();
    if (formIsValid()) {
      console.log(
        signup.username.value,
        signup.email.value,
        signup.password.value,
        signup.confirmPassword.value
      );
    }
  };

  const inputHandler = (event) => {
    const { name, value } = event.target;
    const data = {
      ...signup,
      [name]: {
        ...signup[name],
        value: value,
        isValid: true,
        message: null,
      },
    };
    setSignup(data);
  };

  const resetValidation = () => {
    const state = JSON.parse(JSON.stringify(signup));
    for (let key in state) {
      if (state[key].hasOwnProperty("isValid")) {
        (state[key].isValid = true), (state[key].message = "");
      }
    }
    setSignup(state);
  };

  const formIsValid = () => {
    const username = { ...signup.username };
    const email = { ...signup.email };
    const password = { ...signup.password };
    const confirmPassword = { ...signup.confirmPassword };
    let isGood = true;

    if (!validator.isLength(username.value, { min: 6, max: undefined })) {
      username.isValid = false;
      username.message = "Username at least 6 characters";
      isGood = false;
    }
    if (!validator.isEmail(email.value)) {
      email.isValid = false;
      email.message = "Email isn't valid";
      isGood = false;
    }
    if (!validator.isLength(password.value, { min: 6, max: undefined })) {
      password.isValid = false;
      password.message = "Password at least 6 characters";
      isGood = false;
    }
    if (validator.isEmpty(confirmPassword.value)) {
      confirmPassword.isValid = false;
      confirmPassword.message = "Password can't be empty";
      isGood = false;
    }
    if (!validator.equals(confirmPassword.value, password.value)) {
      confirmPassword.isValid = false;
      confirmPassword.message = "Password isn't matches";
      isGood = false;
    }

    if (!isGood) {
      setSignup({ username, email, password, confirmPassword });
    }

    return isGood;
  };

  const switchToSignin = useCallback(() => {
    props.viewed();
    resetValidation();
  }, [props, resetValidation]);

  const { username, email, password, confirmPassword } = signup;
  const usernameInvalid = cx({ "is-invalid": !username.isValid });
  const emailInvalid = cx({ "is-invalid": !email.isValid });
  const passwordInvalid = cx({ "is-invalid": !password.isValid });
  const confirmInvalid = cx({ "is-invalid": !confirmPassword.isValid });

  return (
    <motion.div initial="initial" animate="in" exit="out" variants={Fade}>
      <Container>
        <Row className="justify-content-md-center">
          <Col>
            <h2 className={`text-center ${style.ff_yeseva}`}>
              Welcome Recruiters.
            </h2>
            <p className="text-center text-muted lh-15">
              <small>
                Register as company to start Recruiting hundred of workers more
                easier, faster and more accurate.
              </small>
            </p>
          </Col>
        </Row>

        <Col className="mx-auto mt-1">
          <Form onSubmit={submitHandler}>
            <Form.Row>
              <Row className="row-cols-2">
                <Form.Group as={Col}>
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    placeholder="Username"
                    onChange={inputHandler}
                    className={usernameInvalid}
                    value={username.value}
                  />
                  <small className="form-text text-muted mt-0 mb-n2">
                    {username.message}
                  </small>
                </Form.Group>

                <Form.Group as={Col}>
                  <Form.Label>Email Address</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    placeholder="Email Address"
                    onChange={inputHandler}
                    className={emailInvalid}
                    value={email.value}
                  />
                  <small className="form-text text-muted mt-0 mb-n2">
                    {email.message}
                  </small>
                </Form.Group>

                <Form.Group as={Col}>
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    placeholder="Enter Password"
                    onChange={inputHandler}
                    className={passwordInvalid}
                    value={password.value}
                  />
                  <small className="form-text text-muted mt-0 mb-n2">
                    {password.message}
                  </small>
                </Form.Group>

                <Form.Group as={Col}>
                  <Form.Label>Confirm Password</Form.Label>
                  <Form.Control
                    type="password"
                    name="confirmPassword"
                    placeholder="Confirm Password"
                    onChange={inputHandler}
                    className={confirmInvalid}
                    value={confirmPassword.value}
                  />
                  <small className="form-text text-muted mt-0 mb-n2">
                    {confirmPassword.message}
                  </small>
                </Form.Group>
              </Row>
              <Button
                block
                variants="primary"
                type="submit"
                className="mt-3 mb-3"
              >
                Register
              </Button>
            </Form.Row>
          </Form>
        </Col>

        <Row className="justify-content-md-center">
          <Col>
            <p className="text-center text-muted lh-15">
              <small>
                To make Automatch work, we log user data and share it with
                service providers. Click{" "}
              </small>
              <small>
                <span
                  className={`${`text-info`} ${style.hov_pointer} `}
                  onClick={switchToSignin}
                >
                  {" "}
                  “Sign in”{" "}
                </span>
              </small>
              <small>
                above to accept Automatch’s Terms of Service &amp; Privacy
                Policy.
              </small>
            </p>
          </Col>
        </Row>
      </Container>
    </motion.div>
  );
};

export default Signin;
